//Display Subclasses

function domainDisplay(x) {
  document.getElementById("lvl1").innerHTML = c1[2].lvl1;
  document.getElementById("lvl2").innerHTML = c1[2].lvl2;
  document.getElementById("lvl6").innerHTML = c1[2].lvl6;
  document.getElementById("lvl17").innerHTML = c1[2].lvl17;
  document.getElementById("lvl1").innerHTML += divineDomains[x].divineDomain;
  document.getElementById("lvl2").innerHTML += divineDomains[x].divineDomainFeature1;
  document.getElementById("lvl6").innerHTML += divineDomains[x].divineDomainFeature2;
  document.getElementById("lvl17").innerHTML += divineDomains[x].divineDomainFeature3;
}
